import React, { ReactElement, type PropsWithChildren } from 'react'
import { type Education, type WorkExperience } from './ResumeContents'
import { ModalContext } from '../Resume'

function formatDate (start: Date, end?: Date): string {
  const startDate = (start.getMonth() + 1).toString() + '/' + start.getFullYear().toString()
  let endDate = 'Present'
  if (end != null) {
    endDate = end.getMonth().toString().concat('/', end.getFullYear().toString())
  }
  return startDate.concat(' - ', endDate)
}

export function GridItem ({ children }: PropsWithChildren): JSX.Element {
  return (<div className='px-5 justify-center'>{children}</div>)
}

export function Title ({ children }: PropsWithChildren): JSX.Element {
  return (<h2 className='font-semibold text-left px-5 pt-3'>{children}</h2>)
}

export function RightItem ({ children }: PropsWithChildren): JSX.Element {
  return (<h2 className='font-normal text-right pr-4 truncate pt-3'>{children}</h2>)
}

export function LeftItem ({ children }: PropsWithChildren): JSX.Element {
  return (<h2 className='font-normal text-left pl-2'>{children}</h2>)
}

export function WorkExperienceItem ({ workExp }: { workExp: WorkExperience }): ReactElement {
  const { setModalOpen } = React.useContext(ModalContext)
  return (
    <div className='bg-stone-800 text-slate-50 sm:mx-2 md:mx-5 mb-20 overflow-auto pb-20 hover:scale-105' onClick={() => { setModalOpen(workExp) }}>
        <div className='grid grid-cols-2'>
            <Title>{workExp.name}</Title>
            <RightItem>{workExp.location}</RightItem>
        </div>
        <GridItem>{formatDate(workExp.startDate, workExp.endDate)}</GridItem>
    </div>)
}

export function EducationItem ({ edu }: { edu: Education }): JSX.Element {
  return (<div className='bg-stone-800 text-slate-50 sm:mx-2 md:mx-5 mb-20 overflow-auto pb-20 hover:scale-105'>
        <div className='grid grid-cols-2'>
            <Title>{edu.name}</Title>
            <RightItem>{edu.location}</RightItem>
        </div>
        <GridItem>{formatDate(edu.startDate, edu.endDate)}</GridItem>
    </div>)
}
